export function selectAsync(selector) {
  return new Promise(function (resolve, reject) {
    my.createSelectorQuery().select(selector).boundingClientRect().exec(function (ret) {
      if (ret && ret[0]) {
        resolve(ret[0]);
      }

      reject('Cannot selector element');
    });
  });
}
export function selectAllAsync(selector) {
  return new Promise(function (resolve, reject) {
    my.createSelectorQuery().selectAll(selector).boundingClientRect().exec(function (ret) {
      if (ret && ret[0]) {
        resolve(ret[0]);
      }

      reject('Cannot selector element');
    });
  });
}