import { isHasValue } from './validate';

function normalize(text) {
  return ("" + text).normalize('NFD').replace(/[\u0300-\u036f]/g, '').replace(/đ/g, 'd').replace(/Đ/g, 'D');
}

export default function compareNormalize(a, b) {
  if (!isHasValue(a) || !isHasValue(b)) {
    return true;
  }

  var normalizeA = normalize(a).toLowerCase();
  var normalizeB = normalize(b).toLowerCase();
  return normalizeA.includes(normalizeB);
}